import Nav from "../components/nav/Nav";
import Button from "../components/button/Button";


const Country=()=>{
    const [schools,setSchools]=useState([]);
    const [suggested,setSuggested]=useState([]);
    const [school,setSchool]=useState("");
    const navigate=useNavigate();

    useEffect(()=>{
        getSchools();
       getSuggested();
    },[])

    const getSchools=()=>{
        fetch('http://localhost:3000/schools')
        .then(res => res.json())
        .then(data=>{
            if(data.status!=="found"){
                console.log("No data");
            }else{
                console.log("data found");
                setSchools(data.data);
                console.log(data.data);
            }
        })
    }

    const getSuggested=()=>{
        let selectedCountry=localStorage.getItem("country");
        let url="http://localhost:3000/schools/suggested/";
        let queryString=url+selectedCountry;
        fetch(queryString)
        .then(res => res.json())
        .then(data=>{
            if(data.status!=="found"){
                console.log("No data");
            }else{
                console.log("data found");
                setSuggested(data.data);
                console.log(data.data);
            }
        })
    }

    const handleChange=(e)=>{
        setSchool(e.target.value);
        console.log("handle changed to ",e.target.value);
    }

    const saveSchool=()=>{
        localStorage.setItem("country",school);
        console.log("local storage:",localStorage.getItem("school"));
        navigate("/programs");

    }

    return(
        <div className="container-box">
