import { React, useState, useEffect} from "react";
import { useNavigate } from "react-router-dom";

const Verify=()=>{
    const [code, setCode]=useState({id:"",vcode:""})

useEffect(() => {
    console.log(code.id+":"+code.vcode);
    }, [code]);
    const navigate=useNavigate();
    const verifyUser=(event)=>{
        event.preventDefault();
        const id=localStorage.getItem("id");
        setCode({...code,id:id});
        console.log(code);
        fetch('http://localhost:3000/signup/verify',{
            method:"POST",
            body:JSON.stringify(code),
            headers:{
                'Content-type':'application/json'
            }
        })
        .then(res => res.json)
        .then(data=>{
            if(data.status!=="success"){
                console.log("Invalid code");
            }else{
                console.log("navigating to login page");
                navigate("/login");
            }
        })

    }

    return(
        <div>
            <form onSubmit={verifyUser}>
                <input type="text" onChange={(e)=> setCode({...code,vcode:e.target.value})} />
                <button className="signupBtn">Verify</button>
            </form>
        </div>
    )
}
export default Verify;
