import { Component, React } from "react";
import Button from "../components/Button";
import Textbox from "../components/Textbox";

class Signup extends Component{
    constructor(props){
        super(props)
        this.state={
            user:{
                fullName:'',
                dob:'',
                email:'',
                phone:'',
                password:'',
                cpassword:''
            }
        }
    }
    inputDataChange= (event)=>{
        this.setState({
            user:{
                fullName:event.target.value,
                dob:event.target.value,
                email:event.target.value,
                phone:event.target.value,
                password:event.target.value,
                cpassword:event.target.value
            }
        },()=>console.log(event.target.value))   
    }
    signupStudent=()=>{
        console.log("Signup Successful");
        document.getElementsById('nameClass').style.visibility='hidden';
        document.getElementsById('emailClass').style.visibility='hidden';

    }
    render(){
        return(
            <div className="container-box cg-background-clr">
                <h3>Hello! <br/> Sign up to get started.</h3>
                <div className="formSection">
                    <div className="nameClass" id="nameClass">
                        <Textbox label="Full name" name={this.state.fullName} inputDataChange={this.inputDataChange}/>
                        <Button name="Next" onClick={this.signupStudent} />
                    </div>
                    <div className="emailClass" id="emailClass">
                        <Textbox label="Email" name={this.state.fullName} inputDataChange={this.inputDataChange}/>
                    </div>
