import { Component, React } from "react";
import Button from "../components/Button";
import Textbox from "../components/Textbox";

class Signup extends Component{
    constructor(props){
        super(props)
        this.state={
            user:{
                fullName:'',
                dob:'',
                email:'',
                phone:'',
                password:'',
                cpassword:''
            }
        }
    }
    inputDataChange= (event)=>{
        this.setState({
            user:{
                fullName:event.target.value,
                dob:event.target.value,
                email:event.target.value,
                phone:event.target.value,
                password:event.target.value,
                cpassword:event.target.value
            }
        },()=>console.log(event.target.value))   
    }
    nextButton=()=>{
        console.log("next button clicked");
        const nodeList=document.getElementById("formBox").childNodes;
        console.log(nodeList);
        for(let i=0;i<nodeList.length;i++){
            if(nodeList[i].className==="active"){
                if(i===nodeList.length-1){
                    break;
                }
                nodeList[i+1].className="active";
                nodeList[i].className="inactive";     
                break;
            }

        }
    }
    backButton=()=>{
        console.log("back button clicked");
        const nodeList=document.getElementById("formBox").childNodes;
        console.log(nodeList);
        for(let i=0;i<nodeList.length;i++){
            if(nodeList[i].className==="active"){
                if(i===0){
                    break;
                }
                nodeList[i-1].className="active";
                nodeList[i].className="inactive";     
                break;
            }
        }
    }
    render(){
        return(
            <div className="container-box cg-background-clr">
                <div className="top">
                    <h3>Hello! <br/> Sign up to get started.</h3>
                </div>
                <div className="bottom-bg">
                    <div className="formSection" id="formBox">
                        <div className="active">
                            <Textbox label="Full name" name={this.state.fullName} inputDataChange={this.inputDataChange}/>
                        </div>
                        <div className="inactive">
                            <Textbox label="Email" name={this.state.email} inputDataChange={this.inputDataChange}/>
                        </div>
                        <div className="inactive">
                            <Textbox label="Phone" name={this.state.phone} inputDataChange={this.inputDataChange}/>
                        </div>
                        <div className="inactive">
                            <Textbox label="Date of Birth" name={this.state.dob} inputDataChange={this.inputDataChange}/>
                        </div>
                        <div className="inactive">
                            <Textbox label="Password" name={this.state.password} inputDataChange={this.inputDataChange}/>
                        </div>
                        <div className="inactive">
                            <Textbox label="Confirm password" name={this.state.cpassword} inputDataChange={this.inputDataChange}/>
                        </div>
                    </div>
                    <div className="bottom">
                        <Button name="Back" onClick={this.backButton} />
                        <Button name="Next" onClick={this.nextButton} />
                    </div>
                </div>
            </div>
            )
        }
}
export default Signup;
