import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import NavButton from "./NavButton";
import "./nav.css";
import home from "../assets/images/home.svg";
import peers from "../assets/images/peers.svg";
import search from "../assets/images/search.svg";
import notif from "../assets/images/notif.svg";
import profile from "../assets/images/profile.svg";

const Nav=(props)=>{

    const navigate=useNavigate();
    const [className,setClassName]=useState({
        home:"inactive",
        peers:"inactive",
        search:"inactive",
        notif:"inactive",
        profile:"inactive"
    });
    const [btnLabel,setBtnLabel]=useState({
        home:"",
        peers:"",
        search:"",
        notif:"",
        profile:""
    });

    useEffect(()=>{
        if(props.pageName==="home"){
            setClassName({
                home:"navBtnActive",
                peers:"inactive",
                search:"inactive",
                notif:"inactive",
                profile:"inactive"
            })
            setBtnLabel({
                home:"Home",
                peers:"",
                search:"",
                notif:"",
                profile:""
            })
        }
        if(props.pageName==="peers"){
            setClassName({
                home:"inactive",
                peers:"navBtnActive",
                search:"inactive",
                notif:"inactive",
                profile:"inactive"
            })
            setBtnLabel({
                home:"",
                peers:"Peers",
                search:"",
                notif:"",
                profile:""
            })
        }
        if(props.pageName==="search"){
            setClassName({
                home:"inactive",
                peers:"inactive",
                search:"navBtnActive",
                notif:"inactive",
                profile:"inactive"
            })
            setBtnLabel({
                home:"",
                peers:"",
                search:"Search",
                notif:"",
                profile:""
            })
        }
        if(props.pageName==="notif"){
            setClassName({
                home:"inactive",
                peers:"inactive",
                search:"inactive",
                notif:"navBtnActive",
                profile:"inactive"
            })
            setBtnLabel({
                home:"",
                peers:"",
                search:"",
                notif:"Notif",
                profile:""
            })
        }
        if(props.pageName==="profile"){
            setClassName({
                home:"inactive",
                peers:"inactive",
                search:"inactive",
                notif:"inactive",
                profile:"navBtnActive"
            })
            setBtnLabel({
                home:"",
                peers:"",
                search:"",
                notif:"",
                profile:"Profile"
            })
        }
    },[props.pageName]);


//functions for navigating pages
    const navigateHome=()=>{
        console.log("Home button clicked");
        navigate("/studentHome");
    }

    const navigatePeers=()=>{
        console.log("Connections button clicked");
        navigate("/studentPeers");
    }  

    const navigateSearch=()=>{
        console.log("Search button clicked");
        navigate("/search");
    }  

    const navigateNotif=()=>{
        console.log("Notifications button clicked");
        navigate("/notif");
    } 

    const navigateProfile=()=>{
        console.log("Profile button clicked");
        navigate("/profile");
    }   

    return(
        <nav>
            <ul>
                <li><NavButton className={className.home} label={btnLabel.home} name="homeBtn" icon={home} onClick={navigateHome} /></li>
                <li><NavButton className={className.peers} label={btnLabel.peers} name="peersBtn" icon={peers} onClick={navigatePeers}/></li>
                <li><NavButton className={className.search} label={btnLabel.search} name="searchBtn" icon={search} onClick={navigateSearch} /></li>
                <li><NavButton className={className.notif} label={btnLabel.notif} name="notiBtn" icon={notif} onClick={navigateNotif}/></li>
                <li><NavButton className={className.profile} label={btnLabel.profile} name="profileBtn" icon={profile} onClick={navigateProfile}/></li>
            </ul>
        </nav>
    )    
}
export default Nav;
