import React from "react";
import Radio from "../../../components/radio/Radio";
import Nav from "../../../components/nav/Nav";
import Button from "../../../components/button/Button";


const Field=()=>{
    const [field,setField]=useState("computerScience");
    const navigate=useNavigate();
    const handleChange=(e)=>{
        setField(e.target.value);
        console.log("handle changed to ",e.target.value);
    }
    const saveField=()=>{
        console.log("Save Field");
        localStorage.setItem("field",field);
        navigate("/career");
    }

    return(
        <div className="container-box">
             <h3>What was your field of study? </h3>
            <Radio id="computerScience" name="field" value="computerScience" label="Maths & Computer Science" onChange={handleChange} />
            <Radio id="socialScience" name="field" value="socialScience" label="Social Sciences" onChange={handleChange}/>
            <Radio id="business" name="field" value="business" label="Business & Management" onChange={handleChange}/>
            <Radio id="fineArts" name="field" value="fineArts" label="Fine & Applied Arts" onChange={handleChange}/>
            <Radio id="education" name="field" value="computerScience" label="Education" onChange={handleChange}/>
            <Button className="signupBtn" label="Next" onClick={saveField} />
            <Nav pageName="home"/>
        </div>
    )
}

export default Field;
